package baekStep3;

import java.util.Scanner;

public class step10950 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Scanner sc = new Scanner(System.in);
		
		int row = sc.nextInt();
		int i=1;
		while(i<=row){
			int a = sc.nextInt();
			int b = sc.nextInt();
			i++;
			System.out.println(a+b);
		}
		
	}

}
